import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5002';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

export const fetchDashboardMetrics = async (startDate, endDate) => {
  try {
    const response = await api.get('/dashboard_metrics', {
      params: {
        start: startDate,
        end: endDate,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching dashboard metrics:', error);
    throw error;
  }
};

export const fetchMetricDetails = async (metric, startDate, endDate) => {
  try {
    const response = await api.get('/dashboard_metric_rows', {
      params: {
        metric,
        start: startDate,
        end: endDate,
      },
    });
    return response.data.rows || [];
  } catch (error) {
    console.error(`Error fetching ${metric} details:`, error);
    throw error;
  }
};

export default api;
